mapper.select:
select g.id,
,tp.money promotionMoney,DATE_FORMAT(tp.start_time,'%Y-%m-%d %H:%i:%s') startTime,DATE_FORMAT(tp.end_time,'%Y-%m-%d %H:%i:%s') endTime ,(g.is_promotion = 1 and now() >= tp.start_time and now() < tp.end_time) isPromotion ,u.name measurementUnit ,ts.name spec_name, ts.id spec_id from t_goods g left join t_unit u on u.id = g.unit_id left join t_promotion tp on tp.goods_id = g.id left join t_specs ts on ts.goods_id = g.id where true and g.owner_id = #{owner} and g.is_delete = 0 and g.status = 1 and g.owner_id = #{owner}
order by sort asc

select g.id,
,tp.money promotionMoney,DATE_FORMAT(tp.start_time,'%Y-%m-%d %H:%i:%s') startTime,DATE_FORMAT(tp.end_time,'%Y-%m-%d %H:%i:%s') endTime ,(g.is_promotion = 1 and now() >= tp.start_time and now() < tp.end_time) isPromotion ,u.name measurementUnit ,ts.name spec_name, ts.id spec_id from t_goods g left join t_unit u on u.id = g.unit_id left join t_promotion tp on tp.goods_id = g.id left join t_specs ts on ts.goods_id = g.id where true
and g.id = #{map.id}
and g.id = ${map.ids}

select  from t_goods
where 1=1

    and category_id = #{categoryId}


    and owner_id = #{owner_id}


    and name like %#{goodsName}%


    limit #{page},20
