mapper.select:
select g.*
from ( select g.id,
from t_goods g where true
) as g
order by field(g.id,${params.ids})
order by g.topTime desc,g.sort asc,g.createTime desc

select a.id,a.name,a.code,a.unit_id as unitId,a.price,a.pics,a.is_promotion isPromotion,a.description,a.sells,a.sort,a.source,a.category_id categoryId,a.creater,a.create_time createTime ,c.`name` as specName ,d.`name` categoryName ,tp.id promotionId ,tp.money promotionMoney,DATE_FORMAT(tp.start_time,'%Y-%m-%d %H:%i:%s') promotionStartTime,DATE_FORMAT(tp.end_time,'%Y-%m-%d %H:%i:%s') promotionEndTime ,(a.is_promotion = 1 and now() >= tp.start_time and now() < tp.end_time) promotionInProgress ,e.after_storage stock ,e.baseline from t_goods a LEFT JOIN t_promotion tp on a.id = tp.goods_id LEFT JOIN (select GROUP_CONCAT(name) name,goods_id from t_specs GROUP BY goods_id) c on a.id = c.goods_id LEFT JOIN t_category d on a.category_id = d.id LEFT JOIN t_goods_storage e on a.id = e.goods_id where 1=1
having true and promotionInProgress = #{params.promotionInProgress}
order by a.top_time desc,a.sort asc,a.create_time desc
