mapper.select:
select g.*

    from (
    select
    g.id,

    from t_goods g
    where true

    and g.id = #{params.id}


        and g.owner_id = #{params.ownerId}


        and g.code = #{params.code}


        and g.id in (${params.ids})


        and gs.id in (${params.specId})


        and g.is_delete = #{params.isDelete}


        and g.update_time > #{params.lastUpdateTime}


        and g.name like '%${params.like_name}%'




            order by field(g.id,${params.ids})



            order by g.sort asc,g.create_time desc



) as g




        order by field(g.id,${params.ids})



        order by g.sort asc,g.createTime desc

select a.id,a.name,a.code,a.unit_id as unitId,a.price,a.pics,a.is_promotion isPromotion,a.description,a.sells,a.sort,a.source,a.category_id categoryId,a.creater,a.create_time createTime,b.start_time startTime,b.end_time endTime,c.`name` as specName ,d.`name` categoryName, e.after_storage stock ,e.baseline from t_goods a LEFT JOIN t_promotion b on a.id = b.goods_id LEFT JOIN (select GROUP_CONCAT(name) name,goods_id from t_specs GROUP BY goods_id) c on a.id = c.goods_id LEFT JOIN t_category d on a.category_id = d.id LEFT JOIN t_goods_storage e on a.id = e.goods_id where 1=1
and a.category_id = #{param.categoryId}
and a.owner_id = #{param.owner_id}
and a.name like '%${param.name}%'
