mapper.select:
select

,b.no as diffNo, b.money as diffTicketMoney, b.pic as diffPic, b.money_diff as diffMoney, b.status as diffStatus, b.refund_by as diffRefundBy, b.refund_at as diffRefundAt, b.refund_status as diffRefundStatus, b.refund_response as diffRefundResponse
,ta.phone phone,ta.owner_id ownerId,ta.name name,ta.province province,ta.detail detail,ta.is_default isDefault,ta.is_delete isDelete,DATE_FORMAT(ta.created_time,'%Y-%m-%d %H:%i:%s') createdTime,ta.creater creater,DATE_FORMAT(ta.deleted_time,'%Y-%m-%d %H:%i:%s') deletedTime
,tr.id id,tr.order_no orderNo,tr.reason reason,tr.status status,tr.order_money orderMoney,tr.response response,DATE_FORMAT(tr.create_time,'%Y-%m-%d %H:%i:%s') createTime,tr.creater creater
from t_order a
left join t_order_money_diff b on a.order_no = b.order_no
left join t_address ta on ta.id = a.address_id
left join t_refund tr on tr.order_no = a.order_no
where true

    and a.id = #{map.id}


    and a.order_no = #{map.order_no}


    and a.source = #{map.source}


    and a.pickup_no = #{map.pickup_no}


    and a.money_channel = #{map.money_channel}


    and a.creater = #{map.creater}


    and a.owner_id = #{map.owner_id}


    and a.type = #{map.type}


    and a.status = #{map.status}

select ,b.`name` ,b.phone ,b.province as address,c.`status` as 'refundStatus', d.money as diffMoney,d.money_diff as moneyDiff,d.pay_order_no as payOrderNo,d.`status` as 'diffStatus' from t_order a LEFT JOIN t_address b on a.address_id = b.id LEFT JOIN t_refund c on a.order_no = c.order_no LEFT JOIN t_order_money_diff d on a.order_no = d.order_no where 1=1
and a.status = #{status}
and a.owner_id = #{owner_id}
limit #{page},20
