mapper.select:
select
id,

from t_order_money_diff tomd where true

    and tomd.id = #{map.id}


    and tomd.pay_order_no = #{map.payOrderNo}


    and tomd.owner_id = #{map.ownerId}


    and tomd.order_no = #{map.orderNo}


    and tomd.money = #{map.money}


    and tomd.money_diff = #{map.moneyDiff}


    and tomd.pic = #{map.pic}


    and tomd.created_time = #{map.createdTime}


    and tomd.creater = #{map.creater}


    and tomd.status = #{map.status}


    and tomd.pay_time = #{map.payTime}


    and tomd.pay_response = #{map.payResponse}


    and tomd.is_delete = #{map.isDelete}
