mapper.select:
select

from t_owner_config toc where true

    and toc.id = #{map.id}


    and toc.owner_id = #{map.ownerId}


    and toc.is_delivery = #{map.isDelivery}


    and toc.is_self_pickup = #{map.isSelfPickup}


    and toc.is_reach_pay = #{map.isReachPay}


    and toc.delivery_distance = #{map.deliveryDistance}


    and toc.order_phone = #{map.orderPhone}

mapper.update:
update t_owner_config

    set is_delivery = #{isDelivery}


    set order_phone = #{orderPhone}

where owner_id = #{ownerId}
