mapper.select:
select

from t_owner_temp tot where true

    and tot.id = #{map.id}


    and tot.shop_id = #{map.shopId}


    and tot.app_id = #{map.appId}


    and tot.app_secret = #{map.appSecret}


    and tot.app_name = #{map.appName}


    and tot.app_qr = #{map.appQr}


    and tot.owner_id = #{map.ownerId}


    and tot.acct_name = #{map.acctName}


    and tot.name = #{map.name}


    and tot.phone = #{map.phone}


    and tot.owner_data_status = #{map.ownerDataStatus}


    and tot.wxapp_data_status = #{map.wxappDataStatus}


    and tot.owner_data_auditor = #{map.ownerDataAuditor}


    and tot.wxapp_data_auditor = #{map.wxappDataAuditor}


    and tot.owner_refuser = #{map.ownerRefuser}


    and tot.owner_refuser_reason = #{map.ownerRefuserReason}


    and tot.wxapp_refuser = #{map.wxappRefuser}


    and tot.owner_is_valid = #{map.ownerIsValid}


    and tot.is_verify = #{map.isVerify}
